import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import './Mahjong.css';

function Mahjong() {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingRecord, setEditingRecord] = useState(null);
  const [filters, setFilters] = useState({
    result_type: ''
  });

  const [formData, setFormData] = useState({
    game_date: new Date().toISOString().split('T')[0],
    win_amount: ''
  });

  useEffect(() => {
    fetchRecords();
  }, [filters]);

  const fetchRecords = async () => {
    setLoading(true);
    try {
      const params = {};
      if (filters.result_type) params.result_type = filters.result_type;

      const response = await api.get('/mahjong', { params });
      if (response.data.success) {
        setRecords(response.data.data);
      }
    } catch (error) {
      console.error('获取记录失败:', error);
      alert('获取记录失败');
    } finally {
      setLoading(false);
    }
  };

  const handleOpenModal = (record = null) => {
    if (record) {
      setEditingRecord(record);
      setFormData({
        game_date: record.game_date,
        win_amount: record.win_amount
      });
    } else {
      setEditingRecord(null);
      setFormData({
        game_date: new Date().toISOString().split('T')[0],
        win_amount: ''
      });
    }
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditingRecord(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.win_amount) {
      alert('请填写盈亏金额');
      return;
    }

    const data = {
      game_date: formData.game_date,
      game_time: '',
      players: ['我'],
      scores: [0],
      win_amount: parseFloat(formData.win_amount),
      game_type: '',
      location: '',
      notes: ''
    };

    try {
      if (editingRecord) {
        await api.put(`/mahjong/${editingRecord.id}`, data);
        alert('记录更新成功');
      } else {
        await api.post('/mahjong', data);
        alert('记录添加成功');
      }
      handleCloseModal();
      fetchRecords();
    } catch (error) {
      console.error('保存失败:', error);
      alert(error.response?.data?.message || '保存失败');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('确定要删除这条记录吗？')) return;

    try {
      await api.delete(`/mahjong/${id}`);
      alert('删除成功');
      fetchRecords();
    } catch (error) {
      console.error('删除失败:', error);
      alert('删除失败');
    }
  };

  const resetFilters = () => {
    setFilters({
      result_type: ''
    });
  };

  if (loading) {
    return <div className="loading">加载中...</div>;
  }

  return (
    <div className="mahjong-page">
      <div className="page-header">
        <h2 className="page-title">麻将记录</h2>
        <button className="add-button" onClick={() => handleOpenModal()}>
          ➕ 添加记录
        </button>
      </div>

      <div className="filters-section">
        <div className="filter-group">
          <label>结果</label>
          <select
            value={filters.result_type}
            onChange={(e) => setFilters({ ...filters, result_type: e.target.value })}
          >
            <option value="">全部</option>
            <option value="win">赢</option>
            <option value="lose">输</option>
          </select>
        </div>
        <button className="reset-button" onClick={resetFilters}>
          重置筛选
        </button>
      </div>

      <div className="records-table-container">
        <table className="records-table">
          <thead>
            <tr>
              <th>日期</th>
              <th>盈亏</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {records.length === 0 ? (
              <tr>
                <td colSpan="3" className="no-data">暂无记录</td>
              </tr>
            ) : (
              records.map((record) => (
                <tr key={record.id}>
                  <td>{record.game_date}</td>
                  <td>
                    <span className={`amount ${record.win_amount >= 0 ? 'win' : 'lose'}`}>
                      {record.win_amount >= 0 ? '+' : ''}¥{record.win_amount.toFixed(2)}
                    </span>
                  </td>
                  <td>
                    <div className="action-buttons">
                      <button
                        className="edit-btn"
                        onClick={() => handleOpenModal(record)}
                      >
                        编辑
                      </button>
                      <button
                        className="delete-btn"
                        onClick={() => handleDelete(record.id)}
                      >
                        删除
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {showModal && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>{editingRecord ? '编辑记录' : '添加记录'}</h3>
              <button className="close-btn" onClick={handleCloseModal}>×</button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label>游戏日期 *</label>
                  <input
                    type="date"
                    value={formData.game_date}
                    onChange={(e) => setFormData({ ...formData, game_date: e.target.value })}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>游戏时间</label>
                  <input
                    type="time"
                    value={formData.game_time}
                    onChange={(e) => setFormData({ ...formData, game_time: e.target.value })}
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>游戏类型</label>
                  <input
                    type="text"
                    placeholder="如：血战、血流"
                    value={formData.game_type}
                    onChange={(e) => setFormData({ ...formData, game_type: e.target.value })}
                  />
                </div>
                <div className="form-group">
                  <label>地点</label>
                  <input
                    type="text"
                    placeholder="游戏地点"
                    value={formData.location}
                    onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                  />
                </div>
              </div>

              <div className="players-section">
                <div className="section-header">
                  <label>玩家和分数 *</label>
                  <button type="button" className="add-player-btn" onClick={addPlayerField}>
                    ➕ 添加玩家
                  </button>
                </div>
                {formData.players.map((player, index) => (
                  <div key={index} className="player-row">
                    <input
                      type="text"
                      placeholder={`玩家 ${index + 1}`}
                      value={player}
                      onChange={(e) => updatePlayer(index, e.target.value)}
                    />
                    <input
                      type="number"
                      step="0.01"
                      placeholder="分数"
                      value={formData.scores[index]}
                      onChange={(e) => updateScore(index, e.target.value)}
                    />
                    {formData.players.length > 2 && (
                      <button
                        type="button"
                        className="remove-player-btn"
                        onClick={() => removePlayerField(index)}
                      >
                        ❌
                      </button>
                    )}
                  </div>
                ))}
              </div>

              <div className="form-group">
                <label>盈亏金额 * (正数为赢，负数为输)</label>
                <input
                  type="number"
                  step="0.01"
                  placeholder="输入金额"
                  value={formData.win_amount}
                  onChange={(e) => setFormData({ ...formData, win_amount: e.target.value })}
                  required
                />
              </div>

              <div className="form-group">
                <label>备注</label>
                <textarea
                  rows="3"
                  placeholder="其他备注信息"
                  value={formData.notes}
                  onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
                />
              </div>

              <div className="modal-actions">
                <button type="button" className="cancel-btn" onClick={handleCloseModal}>
                  取消
                </button>
                <button type="submit" className="submit-btn">
                  {editingRecord ? '更新' : '添加'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Mahjong;
