import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import './Statistics.css';

function Statistics() {
  const [stats, setStats] = useState(null);
  const [categoryStats, setCategoryStats] = useState([]);
  const [chartType, setChartType] = useState('expense'); // 'income' Êàñ 'expense'
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStatistics();
    fetchCategoryStatistics();
  }, [startDate, endDate, chartType]);

  const fetchStatistics = async () => {
    try {
      const params = {};
      if (startDate) params.start_date = startDate;
      if (endDate) params.end_date = endDate;
      
      const response = await api.get('/records/statistics', { params });
      if (response.data.success) {
        setStats(response.data.data);
      }
    } catch (error) {
      console.error('Ëé∑ÂèñÁªüËÆ°Â§±Ë¥•:', error);
    }
  };

  const fetchCategoryStatistics = async () => {
    setLoading(true);
    try {
      const params = { type: chartType };
      if (startDate) params.start_date = startDate;
      if (endDate) params.end_date = endDate;
      
      const response = await api.get('/records/statistics/categories', { params });
      if (response.data.success) {
        setCategoryStats(response.data.data);
      }
    } catch (error) {
      console.error('Ëé∑ÂèñÂàÜÁ±ªÁªüËÆ°Â§±Ë¥•:', error);
    } finally {
      setLoading(false);
    }
  };

  const resetFilters = () => {
    setStartDate('');
    setEndDate('');
  };

  if (loading) {
    return <div className="loading">Âä†ËΩΩ‰∏≠...</div>;
  }

  return (
    <div className="statistics-page">
      <h2 className="page-title">ÁªüËÆ°ÂàÜÊûê</h2>

      <div className="filters-section">
        <div className="filter-group">
          <label>ÂºÄÂßãÊó•Êúü</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
        </div>
        <div className="filter-group">
          <label>ÁªìÊùüÊó•Êúü</label>
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </div>
        <button className="reset-button" onClick={resetFilters}>ÈáçÁΩÆ</button>
      </div>

      <div className="stats-cards">
        <div className="stat-card income">
          <div className="stat-header">
            <div className="stat-icon">üí∞</div>
            <div className="stat-title">ÊÄªÊî∂ÂÖ•</div>
          </div>
          <div className="stat-value">¬•{stats?.income?.toFixed(2) || '0.00'}</div>
          <div className="stat-detail">{stats?.incomeCount || 0} Á¨î‰∫§Êòì</div>
        </div>

        <div className="stat-card expense">
          <div className="stat-header">
            <div className="stat-icon">üí∏</div>
            <div className="stat-title">ÊÄªÊîØÂá∫</div>
          </div>
          <div className="stat-value">¬•{stats?.expense?.toFixed(2) || '0.00'}</div>
          <div className="stat-detail">{stats?.expenseCount || 0} Á¨î‰∫§Êòì</div>
        </div>

        <div className="stat-card balance">
          <div className="stat-header">
            <div className="stat-icon">üíµ</div>
            <div className="stat-title">ÂáÄ‰ΩôÈ¢ù</div>
          </div>
          <div className="stat-value">¬•{stats?.balance?.toFixed(2) || '0.00'}</div>
          <div className="stat-detail">
            {stats?.balance >= 0 ? 'ÁõàÂà©' : '‰∫èÊçü'}
          </div>
        </div>
      </div>

      <div className="chart-section">
        <div className="chart-card">
          <h3>Êî∂ÊîØÂØπÊØî</h3>
          <div className="bar-chart">
            <div className="bar-item">
              <div className="bar-label">Êî∂ÂÖ•</div>
              <div className="bar-container">
                <div
                  className="bar income-bar"
                  style={{
                    width: stats?.income > 0
                      ? `${Math.min((stats.income / (stats.income + stats.expense)) * 100, 100)}%`
                      : '0%'
                  }}
                >
                  ¬•{stats?.income?.toFixed(2) || '0.00'}
                </div>
              </div>
            </div>
            <div className="bar-item">
              <div className="bar-label">ÊîØÂá∫</div>
              <div className="bar-container">
                <div
                  className="bar expense-bar"
                  style={{
                    width: stats?.expense > 0
                      ? `${Math.min((stats.expense / (stats.income + stats.expense)) * 100, 100)}%`
                      : '0%'
                  }}
                >
                  ¬•{stats?.expense?.toFixed(2) || '0.00'}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Statistics;
