import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import './MahjongStatistics.css';

function MahjongStatistics() {
  const getCurrentMonth = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    return `${year}-${month}`;
  };

  const getMonthRange = (monthStr) => {
    if (!monthStr) return { startDate: '', endDate: '' };
    const [year, month] = monthStr.split('-');
    const startDate = `${year}-${month}-01`;
    const lastDay = new Date(parseInt(year), parseInt(month), 0).getDate();
    const endDate = `${year}-${month}-${String(lastDay).padStart(2, '0')}`;
    return { startDate, endDate };
  };

  const [stats, setStats] = useState(null);
  const [dailyStats, setDailyStats] = useState([]);
  const [gameTypeStats, setGameTypeStats] = useState([]);
  const [monthlyStats, setMonthlyStats] = useState([]);
  const [selectedMonth, setSelectedMonth] = useState(getCurrentMonth());
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAllStatistics();
  }, [selectedMonth]);

  const fetchAllStatistics = async () => {
    setLoading(true);
    try {
      const { startDate, endDate } = getMonthRange(selectedMonth);
      const params = {};
      if (startDate) params.start_date = startDate;
      if (endDate) params.end_date = endDate;

      const [statsRes, dailyRes, gameTypeRes, monthlyRes] = await Promise.all([
        api.get('/mahjong/statistics', { params }),
        api.get('/mahjong/statistics/daily', { params }),
        api.get('/mahjong/statistics/game-types', { params }),
        api.get('/mahjong/statistics/monthly')
      ]);

      if (statsRes.data.success) setStats(statsRes.data.data);
      if (dailyRes.data.success) setDailyStats(dailyRes.data.data);
      if (gameTypeRes.data.success) setGameTypeStats(gameTypeRes.data.data);
      if (monthlyRes.data.success) setMonthlyStats(monthlyRes.data.data);
    } catch (error) {
      console.error('Ëé∑ÂèñÁªüËÆ°Êï∞ÊçÆÂ§±Ë¥•:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatMonth = (monthStr) => {
    if (!monthStr) return '';
    const [year, month] = monthStr.split('-');
    return `${year}Âπ¥${parseInt(month)}Êúà`;
  };

  const resetFilters = () => {
    setSelectedMonth(getCurrentMonth());
  };

  const getWinRate = () => {
    if (!stats || stats.total_games === 0) return 0;
    return ((stats.win_games / stats.total_games) * 100).toFixed(1);
  };

  const getColor = (index) => {
    const colors = [
      '#667eea', '#764ba2', '#f093fb', '#4facfe',
      '#43e97b', '#fa709a', '#fee140', '#30cfd0'
    ];
    return colors[index % colors.length];
  };

  if (loading) {
    return <div className="loading">Âä†ËΩΩ‰∏≠...</div>;
  }

  return (
    <div className="mahjong-statistics-page">
      <h2 className="page-title">È∫ªÂ∞ÜÁªüËÆ°ÂàÜÊûê</h2>

      <div className="filters-section">
        <div className="filter-group">
          <label>ÈÄâÊã©Êúà‰ªΩ</label>
          <input
            type="month"
            value={selectedMonth}
            onChange={(e) => setSelectedMonth(e.target.value)}
          />
        </div>
        <div className="filter-group">
          <label>ÂΩìÂâçÁªüËÆ°</label>
          <div className="current-month-display">{formatMonth(selectedMonth)}</div>
        </div>
        <button className="reset-button" onClick={resetFilters}>ÈáçÁΩÆ‰∏∫ÂΩìÂâçÊúà</button>
      </div>

      <div className="stats-cards">
        <div className="stat-card total">
          <div className="stat-header">
            <div className="stat-icon">üé≤</div>
            <div className="stat-title">{formatMonth(selectedMonth)}ÊÄªÂú∫Ê¨°</div>
          </div>
          <div className="stat-value">{stats?.total_games || 0} Âú∫</div>
          <div className="stat-detail">
            ËÉú: {stats?.win_games || 0} | Ë¥ü: {stats?.lose_games || 0}
          </div>
        </div>

        <div className="stat-card win">
          <div className="stat-header">
            <div className="stat-icon">üèÜ</div>
            <div className="stat-title">ËÉúÁéá</div>
          </div>
          <div className="stat-value">{getWinRate()}%</div>
          <div className="stat-detail">
            {stats?.win_games || 0} ËÉú / {stats?.total_games || 0} Âú∫
          </div>
        </div>

        <div className="stat-card amount">
          <div className="stat-header">
            <div className="stat-icon">üí∞</div>
            <div className="stat-title">{formatMonth(selectedMonth)}Áõà‰∫è</div>
          </div>
          <div className={`stat-value ${(stats?.total_amount || 0) >= 0 ? 'positive' : 'negative'}`}>
            {(stats?.total_amount || 0) >= 0 ? '+' : ''}¬•{(stats?.total_amount || 0).toFixed(2)}
          </div>
          <div className="stat-detail">
            ÂùáÂú∫: ¬•{(stats?.avg_amount || 0).toFixed(2)}
          </div>
        </div>

        <div className="stat-card extreme">
          <div className="stat-header">
            <div className="stat-icon">üìä</div>
            <div className="stat-title">ÂçïÂ±ÄÊúÄ‰Ω≥/ÊúÄÂ∑Æ</div>
          </div>
          <div className="stat-value small">
            <span className="positive">+¬•{(stats?.max_win || 0).toFixed(2)}</span>
          </div>
          <div className="stat-detail">
            <span className="negative">¬•{(stats?.min_lose || 0).toFixed(2)}</span>
          </div>
        </div>
      </div>

      <div className="charts-grid">
        <div className="chart-section">
          <div className="chart-card">
            <h3>Ê∏∏ÊàèÁ±ªÂûãÁªüËÆ°</h3>
            {gameTypeStats.length === 0 ? (
              <div className="empty-state">ÊöÇÊó†Êï∞ÊçÆ</div>
            ) : (
              <div className="game-type-stats">
                {gameTypeStats.map((item, index) => (
                  <div key={index} className="game-type-item">
                    <div className="game-type-header">
                      <div
                        className="game-type-color"
                        style={{ backgroundColor: getColor(index) }}
                      ></div>
                      <div className="game-type-name">{item.game_type || 'Êú™ÂàÜÁ±ª'}</div>
                      <div className="game-type-games">{item.games}Âú∫</div>
                    </div>
                    <div className="game-type-details">
                      <div className="detail-item">
                        <span className="label">ËÉúÁéá:</span>
                        <span className="value">
                          {item.games > 0 ? ((item.wins / item.games) * 100).toFixed(1) : 0}%
                        </span>
                      </div>
                      <div className="detail-item">
                        <span className="label">ÊÄªÁõà‰∫è:</span>
                        <span className={`value ${item.total_amount >= 0 ? 'positive' : 'negative'}`}>
                          {item.total_amount >= 0 ? '+' : ''}¬•{parseFloat(item.total_amount).toFixed(2)}
                        </span>
                      </div>
                      <div className="detail-item">
                        <span className="label">ÂùáÂú∫:</span>
                        <span className="value">¬•{parseFloat(item.avg_amount).toFixed(2)}</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="chart-section">
          <div className="chart-card">
            <h3>{formatMonth(selectedMonth)}ÊØèÊó•Ë∂ãÂäø</h3>
            {dailyStats.length === 0 ? (
              <div className="empty-state">ÊöÇÊó†Êï∞ÊçÆ</div>
            ) : (
              <div className="daily-chart">
                {dailyStats.map((day, index) => {
                  const maxAmount = Math.max(...dailyStats.map(d => Math.abs(d.amount)));
                  const barHeight = maxAmount > 0 ? (Math.abs(day.amount) / maxAmount) * 100 : 0;
                  
                  return (
                    <div key={index} className="daily-item" title={`${day.date}\nÁõà‰∫è: ¬•${parseFloat(day.amount).toFixed(2)}\nÂú∫Ê¨°: ${day.games}`}>
                      <div className="daily-bar-container">
                        <div
                          className={`daily-bar ${day.amount >= 0 ? 'positive' : 'negative'}`}
                          style={{ height: `${barHeight}%` }}
                        ></div>
                      </div>
                      <div className="daily-label">{day.date.split('-')[2]}</div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="chart-section full-width">
        <div className="chart-card">
          <h3>ÊúàÂ∫¶ÁªüËÆ°</h3>
          {monthlyStats.length === 0 ? (
            <div className="empty-state">ÊöÇÊó†Êï∞ÊçÆ</div>
          ) : (
            <div className="monthly-table">
              <table>
                <thead>
                  <tr>
                    <th>Êúà‰ªΩ</th>
                    <th>Âú∫Ê¨°</th>
                    <th>ËÉúÂú∫</th>
                    <th>Ë¥üÂú∫</th>
                    <th>ËÉúÁéá</th>
                    <th>ÊÄªÁõà‰∫è</th>
                    <th>ÂùáÂú∫</th>
                  </tr>
                </thead>
                <tbody>
                  {monthlyStats.map((month, index) => (
                    <tr key={index}>
                      <td>{formatMonth(month.month)}</td>
                      <td>{month.games}</td>
                      <td className="positive">{month.wins}</td>
                      <td className="negative">{month.loses}</td>
                      <td>
                        {month.games > 0 ? ((month.wins / month.games) * 100).toFixed(1) : 0}%
                      </td>
                      <td>
                        <span className={month.total_amount >= 0 ? 'positive' : 'negative'}>
                          {month.total_amount >= 0 ? '+' : ''}¬•{parseFloat(month.total_amount).toFixed(2)}
                        </span>
                      </td>
                      <td>¬•{parseFloat(month.avg_amount).toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default MahjongStatistics;
