import React, { useState } from 'react';
import { Outlet, Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Layout.css';

function Layout() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [mobileSidebarOpen, setMobileSidebarOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleMobileSidebar = () => {
    setMobileSidebarOpen(!mobileSidebarOpen);
  };

  const closeMobileSidebar = () => {
    setMobileSidebarOpen(false);
  };

  const menuItems = [
    { path: '/dashboard', icon: 'üìä', label: '‰ª™Ë°®Êùø' },
    { path: '/records', icon: 'üìù', label: 'ËÆ∞Ë¥¶ËÆ∞ÂΩï' },
    { path: '/categories', icon: 'üìÅ', label: 'ÂàÜÁ±ªÁÆ°ÁêÜ' },
    { path: '/statistics', icon: 'üìà', label: 'ÁªüËÆ°ÂàÜÊûê' },
    { path: '/mahjong', icon: 'üÄÑ', label: 'È∫ªÂ∞ÜËÆ∞ÂΩï' },
    { path: '/mahjong-statistics', icon: 'üé≤', label: 'È∫ªÂ∞ÜÁªüËÆ°' },
    { path: '/gifts', icon: 'üéÅ', label: 'Á§ºÈáëËÆ∞ÂΩï' },
    { path: '/shopping', icon: 'üì¶', label: 'ÈááË¥≠Ê∏ÖÂçï' }
  ];

  return (
    <div className="layout">
      <aside className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <h2>ËÆ∞Ë¥¶Á≥ªÁªü</h2>
          <button className="sidebar-toggle" onClick={() => setSidebarOpen(!sidebarOpen)}>
            {sidebarOpen ? '‚óÄ' : '‚ñ∂'}
          </button>
        </div>
        <nav className="sidebar-nav">
          {menuItems.map(item => (
            <Link
              key={item.path}
              to={item.path}
              className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
            >
              <span className="nav-icon">{item.icon}</span>
              {sidebarOpen && <span className="nav-label">{item.label}</span>}
            </Link>
          ))}
        </nav>
      </aside>
      <div className="main-content">
        <header className="header">
          <div className="header-left">
            <h1>ËÆ∞Ë¥¶ÁÆ°ÁêÜÁ≥ªÁªü</h1>
          </div>
          <div className="header-right">
            <span className="user-info">Ê¨¢Ëøé, {user?.username}</span>
            <button className="logout-button" onClick={handleLogout}>
              ÈÄÄÂá∫
            </button>
          </div>
        </header>
        <main className="content">
          <Outlet />
        </main>
      </div>
    </div>
  );
}

export default Layout;
