import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import './Statistics.css';

function Statistics() {
  const [stats, setStats] = useState(null);
  const [categoryStats, setCategoryStats] = useState([]);
  const [chartType, setChartType] = useState('expense'); // 'income' Êàñ 'expense'
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStatistics();
    fetchCategoryStatistics();
  }, [startDate, endDate, chartType]);

  const fetchStatistics = async () => {
    try {
      const params = {};
      if (startDate) params.start_date = startDate;
      if (endDate) params.end_date = endDate;
      
      const response = await api.get('/records/statistics', { params });
      if (response.data.success) {
        setStats(response.data.data);
      }
    } catch (error) {
      console.error('Ëé∑ÂèñÁªüËÆ°Â§±Ë¥•:', error);
    }
  };

  const fetchCategoryStatistics = async () => {
    setLoading(true);
    try {
      const params = { type: chartType };
      if (startDate) params.start_date = startDate;
      if (endDate) params.end_date = endDate;
      
      const response = await api.get('/records/statistics/categories', { params });
      if (response.data.success) {
        setCategoryStats(response.data.data);
      }
    } catch (error) {
      console.error('Ëé∑ÂèñÂàÜÁ±ªÁªüËÆ°Â§±Ë¥•:', error);
    } finally {
      setLoading(false);
    }
  };

  const resetFilters = () => {
    setStartDate('');
    setEndDate('');
  };

  // Ëé∑ÂèñÈ¢úËâ≤
  const getColor = (index) => {
    const colors = [
      '#667eea', '#764ba2', '#f093fb', '#4facfe',
      '#43e97b', '#fa709a', '#fee140', '#30cfd0',
      '#a8edea', '#fed6e3', '#ffecd2', '#fcb69f'
    ];
    return colors[index % colors.length];
  };

  // ËÆ°ÁÆóÁôæÂàÜÊØî
  const getPercentage = (value, data) => {
    const total = data.reduce((sum, item) => sum + parseFloat(item.total || 0), 0);
    if (total === 0) return 0;
    return ((parseFloat(value) / total) * 100).toFixed(1);
  };

  // È•ºÁä∂ÂõæÁªÑ‰ª∂
  const PieChart = ({ data }) => {
    const total = data.reduce((sum, item) => sum + parseFloat(item.total || 0), 0);
    if (total === 0) return <div className="empty-state">ÊöÇÊó†Êï∞ÊçÆ</div>;

    let currentAngle = -90; // ‰ªéÈ°∂ÈÉ®ÂºÄÂßã
    const radius = 120;
    const centerX = 150;
    const centerY = 150;

    const paths = data.map((item, index) => {
      const value = parseFloat(item.total || 0);
      const percentage = (value / total) * 100;
      const angle = (percentage / 100) * 360;
      
      const startAngle = currentAngle;
      const endAngle = currentAngle + angle;
      
      const x1 = centerX + radius * Math.cos((startAngle * Math.PI) / 180);
      const y1 = centerY + radius * Math.sin((startAngle * Math.PI) / 180);
      const x2 = centerX + radius * Math.cos((endAngle * Math.PI) / 180);
      const y2 = centerY + radius * Math.sin((endAngle * Math.PI) / 180);
      
      const largeArcFlag = angle > 180 ? 1 : 0;
      
      const pathData = [
        `M ${centerX} ${centerY}`,
        `L ${x1} ${y1}`,
        `A ${radius} ${radius} 0 ${largeArcFlag} 1 ${x2} ${y2}`,
        'Z'
      ].join(' ');

      currentAngle = endAngle;

      return (
        <path
          key={item.id || index}
          d={pathData}
          fill={getColor(index)}
          stroke="#fff"
          strokeWidth="2"
        />
      );
    });

    return (
      <svg width="300" height="300" viewBox="0 0 300 300" className="pie-chart">
        {paths}
      </svg>
    );
  };

  if (loading) {
