        <div className="stat-card balance">
          <div className="stat-header">
            <div className="stat-icon">üíµ</div>
            <div className="stat-title">ÂáÄ‰ΩôÈ¢ù</div>
          </div>
          <div className="stat-value">¬•{stats?.balance?.toFixed(2) || '0.00'}</div>
          <div className="stat-detail">
            {stats?.balance >= 0 ? 'ÁõàÂà©' : '‰∫èÊçü'}
          </div>
        </div>
      </div>

      <div className="charts-grid">
        <div className="chart-section">
          <div className="chart-card">
            <h3>Êî∂ÊîØÂØπÊØî</h3>
            <div className="bar-chart">
              <div className="bar-item">
                <div className="bar-label">Êî∂ÂÖ•</div>
                <div className="bar-container">
                  <div
                    className="bar income-bar"
                    style={{
                      width: stats?.income > 0
                        ? `${Math.min((stats.income / (stats.income + stats.expense)) * 100, 100)}%`
                        : '0%'
                    }}
