import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import './GiftRecords.css';

function GiftRecords() {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingRecord, setEditingRecord] = useState(null);
  const [filters, setFilters] = useState({
    start_date: '',
    end_date: '',
    gift_type: '',
    event_type: '',
    name: ''
  });

  const [formData, setFormData] = useState({
    name: '',
    gift_type: 'é€ç¤¼',
    event_type: 'å©šç¤¼',
    amount: '',
    event_date: new Date().toISOString().split('T')[0],
    relationship: '',
    location: '',
    notes: ''
  });

  const eventTypes = ['å©šç¤¼', 'ä¸§äº‹', 'ä¹”è¿', 'ç”Ÿæ—¥', 'æ»¡æœˆ', 'å‡å­¦', 'å…¶ä»–'];
  const relationships = ['äº²æˆš', 'æœ‹å‹', 'åŒäº‹', 'é‚»å±…', 'åŒå­¦', 'å…¶ä»–'];

  useEffect(() => {
    fetchRecords();
  }, [filters]);

  const fetchRecords = async () => {
    setLoading(true);
    try {
      const params = {};
      if (filters.start_date) params.start_date = filters.start_date;
      if (filters.end_date) params.end_date = filters.end_date;
      if (filters.gift_type) params.gift_type = filters.gift_type;
      if (filters.event_type) params.event_type = filters.event_type;
      if (filters.name) params.name = filters.name;

      const response = await api.get('/gifts', { params });
      if (response.data.success) {
        setRecords(response.data.data);
      }
    } catch (error) {
      console.error('è·å–è®°å½•å¤±è´¥:', error);
      alert('è·å–è®°å½•å¤±è´¥');
    } finally {
      setLoading(false);
    }
  };

  const handleOpenModal = (record = null) => {
    if (record) {
      setEditingRecord(record);
      setFormData({
        name: record.name,
        gift_type: record.gift_type,
        event_type: record.event_type,
        amount: record.amount,
        event_date: record.event_date,
        relationship: record.relationship || '',
        location: record.location || '',
        notes: record.notes || ''
      });
    } else {
      setEditingRecord(null);
      setFormData({
        name: '',
        gift_type: 'é€ç¤¼',
        event_type: 'å©šç¤¼',
        amount: '',
        event_date: new Date().toISOString().split('T')[0],
        relationship: '',
        location: '',
        notes: ''
      });
    }
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditingRecord(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.name.trim() || !formData.amount || parseFloat(formData.amount) <= 0) {
      alert('è¯·å¡«å†™å§“åå’Œæœ‰æ•ˆé‡‘é¢');
      return;
    }

    try {
      const submitData = {
        ...formData,
        amount: parseFloat(formData.amount)
      };

      if (editingRecord) {
        await api.put(`/gifts/${editingRecord.id}`, submitData);
        alert('è®°å½•æ›´æ–°æˆåŠŸ');
      } else {
        await api.post('/gifts', submitData);
        alert('è®°å½•æ·»åŠ æˆåŠŸ');
      }
      
      handleCloseModal();
      fetchRecords();
    } catch (error) {
      console.error('æäº¤å¤±è´¥:', error);
      alert(error.response?.data?.message || 'æäº¤å¤±è´¥');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('ç¡®å®šè¦åˆ é™¤è¿™æ¡è®°å½•å—ï¼Ÿ')) return;

    try {
      await api.delete(`/gifts/${id}`);
      alert('åˆ é™¤æˆåŠŸ');
      fetchRecords();
    } catch (error) {
      console.error('åˆ é™¤å¤±è´¥:', error);
      alert('åˆ é™¤å¤±è´¥');
    }
  };

  const resetFilters = () => {
    setFilters({
      start_date: '',
      end_date: '',
      gift_type: '',
      event_type: '',
      name: ''
    });
  };

  const getTotalGiving = () => {
    return records
      .filter(r => r.gift_type === 'é€ç¤¼')
      .reduce((sum, r) => sum + r.amount, 0)
      .toFixed(2);
  };

  const getTotalReceiving = () => {
    return records
      .filter(r => r.gift_type === 'æ”¶ç¤¼')
      .reduce((sum, r) => sum + r.amount, 0)
      .toFixed(2);
  };

  const getNetAmount = () => {
    return (getTotalReceiving() - getTotalGiving()).toFixed(2);
  };

  return (
    <div className="gift-records-page">
      <div className="page-header">
        <h1 className="page-title">ğŸ ç¤¼é‡‘è®°å½•</h1>
        <button className="add-button" onClick={() => handleOpenModal()}>
          + æ·»åŠ è®°å½•
        </button>
      </div>

      {/* ç»Ÿè®¡å¡ç‰‡ */}
      <div className="stats-cards">
        <div className="stat-card giving">
          <div className="stat-label">é€ç¤¼æ€»é¢</div>
          <div className="stat-value">Â¥{getTotalGiving()}</div>
          <div className="stat-count">{records.filter(r => r.gift_type === 'é€ç¤¼').length} æ¬¡</div>
        </div>
        <div className="stat-card receiving">
          <div className="stat-label">æ”¶ç¤¼æ€»é¢</div>
          <div className="stat-value">Â¥{getTotalReceiving()}</div>
          <div className="stat-count">{records.filter(r => r.gift_type === 'æ”¶ç¤¼').length} æ¬¡</div>
        </div>
        <div className="stat-card net">
          <div className="stat-label">å‡€é¢</div>
          <div className={`stat-value ${parseFloat(getNetAmount()) >= 0 ? 'positive' : 'negative'}`}>
            Â¥{getNetAmount()}
          </div>
          <div className="stat-count">
            {parseFloat(getNetAmount()) >= 0 ? 'æ”¶å…¥å¤§äºæ”¯å‡º' : 'æ”¯å‡ºå¤§äºæ”¶å…¥'}
          </div>
        </div>
      </div>

      {/* ç­›é€‰å™¨ */}
      <div className="filters-section">
        <div className="filter-group">
          <label>å§“å</label>
          <input
            type="text"
            value={filters.name}
            onChange={(e) => setFilters({ ...filters, name: e.target.value })}
            placeholder="æœç´¢å§“å"
          />
        </div>
        <div className="filter-group">
          <label>ç±»å‹</label>
          <select
            value={filters.gift_type}
            onChange={(e) => setFilters({ ...filters, gift_type: e.target.value })}
          >
            <option value="">å…¨éƒ¨</option>
            <option value="é€ç¤¼">é€ç¤¼</option>
            <option value="æ”¶ç¤¼">æ”¶ç¤¼</option>
          </select>
        </div>
        <div className="filter-group">
          <label>äº‹ä»¶ç±»å‹</label>
          <select
            value={filters.event_type}
            onChange={(e) => setFilters({ ...filters, event_type: e.target.value })}
          >
            <option value="">å…¨éƒ¨</option>
            {eventTypes.map(type => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
        </div>
        <div className="filter-group">
          <label>å¼€å§‹æ—¥æœŸ</label>
          <input
            type="date"
            value={filters.start_date}
            onChange={(e) => setFilters({ ...filters, start_date: e.target.value })}
          />
        </div>
        <div className="filter-group">
          <label>ç»“æŸæ—¥æœŸ</label>
          <input
            type="date"
            value={filters.end_date}
            onChange={(e) => setFilters({ ...filters, end_date: e.target.value })}
          />
        </div>
        <button className="reset-button" onClick={resetFilters}>
          é‡ç½®
        </button>
      </div>

      {/* è®°å½•è¡¨æ ¼ */}
      <div className="records-table-container">
        {loading ? (
          <div className="loading">åŠ è½½ä¸­...</div>
        ) : (
          <table className="records-table">
            <thead>
              <tr>
                <th>æ—¥æœŸ</th>
                <th>å§“å</th>
                <th>ç±»å‹</th>
                <th>äº‹ä»¶</th>
                <th>é‡‘é¢</th>
                <th>å…³ç³»</th>
                <th>åœ°ç‚¹</th>
                <th>å¤‡æ³¨</th>
                <th>æ“ä½œ</th>
              </tr>
            </thead>
            <tbody>
              {records.length === 0 ? (
                <tr>
                  <td colSpan="9" className="no-data">
                    æš‚æ— è®°å½•
                  </td>
                </tr>
              ) : (
                records.map(record => (
                  <tr key={record.id}>
                    <td>{record.event_date}</td>
                    <td className="name-cell">{record.name}</td>
                    <td>
                      <span className={`gift-type-badge ${record.gift_type === 'é€ç¤¼' ? 'giving' : 'receiving'}`}>
                        {record.gift_type}
                      </span>
                    </td>
                    <td>
                      <span className="event-type-badge">{record.event_type}</span>
                    </td>
                    <td>
                      <span className={`amount ${record.gift_type === 'é€ç¤¼' ? 'giving' : 'receiving'}`}>
                        {record.gift_type === 'é€ç¤¼' ? '-' : '+'}Â¥{record.amount.toFixed(2)}
                      </span>
                    </td>
                    <td>{record.relationship || '-'}</td>
                    <td>{record.location || '-'}</td>
                    <td className="notes-cell">{record.notes || '-'}</td>
                    <td>
                      <div className="action-buttons">
                        <button
                          className="edit-button"
                          onClick={() => handleOpenModal(record)}
                        >
                          ç¼–è¾‘
                        </button>
                        <button
                          className="delete-button"
                          onClick={() => handleDelete(record.id)}
                        >
                          åˆ é™¤
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        )}
      </div>

      {/* æ¨¡æ€æ¡† */}
      {showModal && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{editingRecord ? 'ç¼–è¾‘è®°å½•' : 'æ·»åŠ è®°å½•'}</h2>
              <button className="close-button" onClick={handleCloseModal}>
                Ã—
              </button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label>å§“å *</label>
                  <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>ç±»å‹ *</label>
                  <select
                    value={formData.gift_type}
                    onChange={(e) => setFormData({ ...formData, gift_type: e.target.value })}
                    required
                  >
                    <option value="é€ç¤¼">é€ç¤¼</option>
                    <option value="æ”¶ç¤¼">æ”¶ç¤¼</option>
                  </select>
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>äº‹ä»¶ç±»å‹ *</label>
                  <select
                    value={formData.event_type}
                    onChange={(e) => setFormData({ ...formData, event_type: e.target.value })}
                    required
                  >
                    {eventTypes.map(type => (
                      <option key={type} value={type}>{type}</option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <label>é‡‘é¢ *</label>
                  <input
                    type="number"
                    step="0.01"
                    min="0"
                    value={formData.amount}
                    onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
                    required
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>æ—¥æœŸ *</label>
                  <input
                    type="date"
                    value={formData.event_date}
                    onChange={(e) => setFormData({ ...formData, event_date: e.target.value })}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>å…³ç³»</label>
                  <select
                    value={formData.relationship}
                    onChange={(e) => setFormData({ ...formData, relationship: e.target.value })}
                  >
                    <option value="">è¯·é€‰æ‹©</option>
                    {relationships.map(rel => (
                      <option key={rel} value={rel}>{rel}</option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label>åœ°ç‚¹</label>
                <input
                  type="text"
                  value={formData.location}
                  onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                  placeholder="ä¾‹å¦‚ï¼šXXé…’åº—"
                />
              </div>

              <div className="form-group">
                <label>å¤‡æ³¨</label>
                <textarea
                  value={formData.notes}
                  onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
                  rows="3"
                  placeholder="è®°å½•ä¸€äº›é¢å¤–ä¿¡æ¯..."
                />
              </div>

              <div className="modal-actions">
                <button type="button" className="cancel-button" onClick={handleCloseModal}>
                  å–æ¶ˆ
                </button>
                <button type="submit" className="submit-button">
                  {editingRecord ? 'æ›´æ–°' : 'æ·»åŠ '}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default GiftRecords;
