import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import './MahjongStatistics.css';

function MahjongStatistics() {
  const getCurrentMonth = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    return `${year}-${month}`;
  };

  const getMonthRange = (monthStr) => {
    if (!monthStr) return { startDate: '', endDate: '' };
    const [year, month] = monthStr.split('-');
    const startDate = `${year}-${month}-01`;
    const lastDay = new Date(parseInt(year), parseInt(month), 0).getDate();
    const endDate = `${year}-${month}-${String(lastDay).padStart(2, '0')}`;
    return { startDate, endDate };
  };

  const [stats, setStats] = useState(null);
  const [records, setRecords] = useState([]);
  const [selectedRecord, setSelectedRecord] = useState(null);
  const [selectedMonth, setSelectedMonth] = useState(getCurrentMonth());
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAllStatistics();
  }, [selectedMonth, selectedYear]);

  const fetchAllStatistics = async () => {
    setLoading(true);
    try {
      const { startDate, endDate } = getMonthRange(selectedMonth);
      const params = {};
      if (startDate) params.start_date = startDate;
      if (endDate) params.end_date = endDate;

      const statsRes = await api.get('/mahjong/statistics', { params });

      if (statsRes.data.success) setStats(statsRes.data.data);

      // Ëé∑ÂèñÂπ¥Â∫¶ÊâÄÊúâËÆ∞ÂΩï
      const yearParams = {
        start_date: `${selectedYear}-01-01`,
        end_date: `${selectedYear}-12-31`
      };
      const recordsRes = await api.get('/mahjong', { params: yearParams });
      if (recordsRes.data.success) {
        setRecords(recordsRes.data.data || []);
      }
    } catch (error) {
      console.error('Ëé∑ÂèñÁªüËÆ°Êï∞ÊçÆÂ§±Ë¥•:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatMonth = (monthStr) => {
    if (!monthStr) return '';
    const [year, month] = monthStr.split('-');
    return `${year}Âπ¥${parseInt(month)}Êúà`;
  };

  const resetFilters = () => {
    setSelectedMonth(getCurrentMonth());
  };

  const getWinRate = () => {
    if (!stats || stats.total_games === 0) return 0;
    return ((stats.win_games / stats.total_games) * 100).toFixed(1);
  };

  const calculateTotalCost = (record) => {
    const winAmount = record.win_amount || 0;
    const tableFee = record.table_fee || 0;
    const taxiFee = record.taxi_fee || 0;
    const cigaretteFee = record.cigarette_fee || 0;
    return winAmount - tableFee - taxiFee - cigaretteFee;
  };

  const handleRecordClick = (record) => {
    setSelectedRecord(record);
  };

  const closeRecordDetail = () => {
    setSelectedRecord(null);
  };

  const getChartData = () => {
    if (records.length === 0) return [];
    // ÊåâÊó•ÊúüÊéíÂ∫è
    return [...records].sort((a, b) => new Date(a.game_date) - new Date(b.game_date));
  };

  const getMaxAbsValue = () => {
    if (records.length === 0) return 100;
    const values = records.map(r => Math.abs(r.win_amount || 0));
    return Math.max(...values, 100);
  };


  if (loading) {
    return <div className="loading">Âä†ËΩΩ‰∏≠...</div>;
  }

  return (
    <div className="mahjong-statistics-page">
      <h2 className="page-title">È∫ªÂ∞ÜÁªüËÆ°ÂàÜÊûê</h2>

      <div className="filters-section">
        <div className="filter-group">
          <label>ÈÄâÊã©Êúà‰ªΩ</label>
          <input
            type="month"
            value={selectedMonth}
            onChange={(e) => setSelectedMonth(e.target.value)}
          />
        </div>
        <div className="filter-group">
          <label>ÈÄâÊã©Âπ¥‰ªΩ</label>
          <select
            value={selectedYear}
            onChange={(e) => setSelectedYear(parseInt(e.target.value))}
          >
            {[2025, 2024, 2023, 2026, 2027].map(year => (
              <option key={year} value={year}>{year}Âπ¥</option>
            ))}
          </select>
        </div>
        <div className="filter-group">
          <label>ÂΩìÂâçÁªüËÆ°</label>
          <div className="current-month-display">{formatMonth(selectedMonth)}</div>
        </div>
        <button className="reset-button" onClick={resetFilters}>ÈáçÁΩÆ‰∏∫ÂΩìÂâçÊúà</button>
      </div>

      <div className="stats-cards">
        <div className="stat-card total">
          <div className="stat-header">
            <div className="stat-icon">üé≤</div>
            <div className="stat-title">{formatMonth(selectedMonth)}ÊÄªÂú∫Ê¨°</div>
          </div>
          <div className="stat-value">{stats?.total_games || 0} Âú∫</div>
          <div className="stat-detail">
            ËÉú: {stats?.win_games || 0} | Ë¥ü: {stats?.lose_games || 0}
          </div>
        </div>

        <div className="stat-card win">
          <div className="stat-header">
            <div className="stat-icon">üèÜ</div>
            <div className="stat-title">ËÉúÁéá</div>
          </div>
          <div className="stat-value">{getWinRate()}%</div>
          <div className="stat-detail">
            {stats?.win_games || 0} ËÉú / {stats?.total_games || 0} Âú∫
          </div>
        </div>

        <div className="stat-card amount">
          <div className="stat-header">
            <div className="stat-icon">üí∞</div>
            <div className="stat-title">{formatMonth(selectedMonth)}Áõà‰∫è</div>
          </div>
          <div className={`stat-value ${(stats?.total_amount || 0) >= 0 ? 'positive' : 'negative'}`}>
            {(stats?.total_amount || 0) >= 0 ? '+' : ''}¬•{(stats?.total_amount || 0).toFixed(2)}
          </div>
          <div className="stat-detail">
            ÂùáÂú∫: ¬•{(stats?.avg_amount || 0).toFixed(2)}
          </div>
        </div>

        <div className="stat-card extreme">
          <div className="stat-header">
            <div className="stat-icon">üìä</div>
            <div className="stat-title">ÂçïÂ±ÄÊúÄ‰Ω≥/ÊúÄÂ∑Æ</div>
          </div>
          <div className="stat-value small">
            <span className="positive">+¬•{(stats?.max_win || 0).toFixed(2)}</span>
          </div>
          <div className="stat-detail">
            <span className="negative">¬•{(stats?.min_lose || 0).toFixed(2)}</span>
          </div>
        </div>
      </div>

      {/* Âπ¥Â∫¶ËæìËµ¢ÊäòÁ∫øÂõæ */}
      <div className="chart-section full-width">
        <div className="chart-card">
          <h3>{selectedYear}Âπ¥Â∫¶ËæìËµ¢Ë∂ãÂäø</h3>
          {records.length === 0 ? (
            <div className="empty-state">ÊöÇÊó†Êï∞ÊçÆ</div>
          ) : (
            <div className="line-chart-container">
              <svg className="line-chart" viewBox="0 0 1000 400" preserveAspectRatio="xMidYMid meet">
                {/* Èõ∂Á∫ø */}
                <line x1="50" y1="200" x2="950" y2="200" stroke="#ddd" strokeWidth="1" strokeDasharray="5,5" />
                
                {/* ÁªòÂà∂ÊäòÁ∫øÂíåËäÇÁÇπ */}
                {(() => {
                  const chartData = getChartData();
                  const maxValue = getMaxAbsValue();
                  const points = chartData.map((record, index) => {
                    const x = 50 + (index / Math.max(chartData.length - 1, 1)) * 900;
                    const y = 200 - (record.win_amount / maxValue) * 150;
                    return { x, y, record };
                  });

                  return (
                    <>
                      {/* ÊäòÁ∫ø */}
                      <polyline
                        points={points.map(p => `${p.x},${p.y}`).join(' ')}
                        fill="none"
                        stroke="#667eea"
                        strokeWidth="2"
                      />
                      
                      {/* ËäÇÁÇπ */}
                      {points.map((point, index) => (
                        <g key={index}>
                          {/* ËäÇÁÇπÂúÜÂúà */}
                          <circle
                            cx={point.x}
                            cy={point.y}
                            r="6"
                            fill={point.record.win_amount >= 0 ? '#27ae60' : '#e74c3c'}
                            stroke="white"
                            strokeWidth="2"
                            style={{ cursor: 'pointer' }}
                            onClick={() => handleRecordClick(point.record)}
                          />
                          {/* ÊÇ¨ÂÅúÊïàÊûú */}
                          <circle
                            cx={point.x}
                            cy={point.y}
                            r="10"
                            fill="transparent"
                            style={{ cursor: 'pointer' }}
                            onClick={() => handleRecordClick(point.record)}
                          />
                        </g>
                      ))}

                      {/* Èõ∂Á∫øÊ†áÁ≠æ */}
                      <text x="25" y="205" fontSize="12" fill="#999">¬•0</text>
                      
                      {/* YËΩ¥Ê†áÁ≠æ */}
                      <text x="10" y="60" fontSize="12" fill="#27ae60">+¬•{maxValue.toFixed(0)}</text>
                      <text x="10" y="350" fontSize="12" fill="#e74c3c">-¬•{maxValue.toFixed(0)}</text>
                    </>
                  );
                })()}
              </svg>
              
              {/* Âõæ‰æã */}
              <div className="chart-legend">
                <div className="legend-item">
                  <div className="legend-color" style={{ backgroundColor: '#27ae60' }}></div>
                  <span>Ëµ¢</span>
                </div>
                <div className="legend-item">
                  <div className="legend-color" style={{ backgroundColor: '#e74c3c' }}></div>
                  <span>Ëæì</span>
                </div>
                <div className="legend-tip">üí° ÁÇπÂáªËäÇÁÇπÊü•ÁúãËØ¶ÊÉÖ</div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* ËÆ∞ÂΩïËØ¶ÊÉÖÂºπÁ™ó */}
      {selectedRecord && (
        <div className="modal-overlay" onClick={closeRecordDetail}>
          <div className="record-detail-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>üìã È∫ªÂ∞ÜËÆ∞ÂΩïËØ¶ÊÉÖ</h3>
              <button className="close-btn" onClick={closeRecordDetail}>√ó</button>
            </div>
            <div className="detail-content">
              <div className="detail-row">
                <span className="detail-label">Êó•ÊúüÔºö</span>
                <span className="detail-value">{selectedRecord.game_date}</span>
              </div>
              <div className="detail-row">
                <span className="detail-label">ËæìËµ¢ÈáëÈ¢ùÔºö</span>
                <span className={`detail-value amount ${selectedRecord.win_amount >= 0 ? 'win' : 'lose'}`}>
                  {selectedRecord.win_amount >= 0 ? '+' : ''}¬•{selectedRecord.win_amount.toFixed(2)}
                </span>
              </div>
              <div className="detail-row">
                <span className="detail-label">Âè∞Ë¥πÔºö</span>
                <span className="detail-value">¬•{(selectedRecord.table_fee || 0).toFixed(2)}</span>
              </div>
              <div className="detail-row">
                <span className="detail-label">ÊâìËΩ¶Ë¥πÔºö</span>
                <span className="detail-value">¬•{(selectedRecord.taxi_fee || 0).toFixed(2)}</span>
              </div>
              <div className="detail-row">
                <span className="detail-label">ÁÉüÈí±Ôºö</span>
                <span className="detail-value">¬•{(selectedRecord.cigarette_fee || 0).toFixed(2)}</span>
              </div>
              <div className="detail-divider"></div>
              <div className="detail-row total">
                <span className="detail-label">ÊÄªÊ∂àË¥πÔºö</span>
                <span className={`detail-value total-cost ${calculateTotalCost(selectedRecord) >= 0 ? 'positive' : 'negative'}`}>
                  {calculateTotalCost(selectedRecord) >= 0 ? '+' : ''}¬•{calculateTotalCost(selectedRecord).toFixed(2)}
                </span>
              </div>
              <div className="detail-formula">
                ÊÄªÊ∂àË¥π = ËæìËµ¢({selectedRecord.win_amount >= 0 ? '+' : ''}{selectedRecord.win_amount.toFixed(2)}) 
                - Âè∞Ë¥π({(selectedRecord.table_fee || 0).toFixed(2)}) 
                - ÊâìËΩ¶Ë¥π({(selectedRecord.taxi_fee || 0).toFixed(2)}) 
                - ÁÉüÈí±({(selectedRecord.cigarette_fee || 0).toFixed(2)})
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default MahjongStatistics;
